import React, { useState } from 'react';
import CreatePollFixed from './components/CreatePollFixed';

function SimpleApp() {
  const [message, setMessage] = useState('Welcome to Private Vote!');
  const [createdPolls, setCreatedPolls] = useState<Array<{question: string; options: string[]}>>([]);
  const [showCreatePoll, setShowCreatePoll] = useState(true);

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        background: 'white',
        padding: '40px',
        borderRadius: '20px',
        boxShadow: '0 20px 60px rgba(0,0,0,0.3)',
        maxWidth: '600px',
        textAlign: 'center'
      }}>
        <h1 style={{ 
          fontSize: '48px', 
          marginBottom: '20px',
          color: '#667eea'
        }}>
          üó≥Ô∏è Private Vote
        </h1>
        <p style={{ 
          fontSize: '20px', 
          color: '#666',
          marginBottom: '30px'
        }}>
          {message}
        </p>
        <div style={{
          background: '#f0f0f0',
          padding: '20px',
          borderRadius: '10px',
          textAlign: 'left'
        }}>
          <h3 style={{ color: '#333', marginBottom: '15px' }}>‚úÖ What's Working:</h3>
          <ul style={{ color: '#666', lineHeight: '1.8' }}>
            <li>‚úÖ Smart Contract - Fully functional</li>
            <li>‚úÖ Frontend Server - Running on localhost</li>
            <li>‚úÖ React Setup - Configured and ready</li>
            <li>‚úÖ TypeScript - Properly set up</li>
            <li>‚è≥ Complex Components - Need fixing</li>
          </ul>
        </div>
        <button 
          onClick={() => setMessage('Thank you for using Private Vote!')}
          style={{
            marginTop: '30px',
            padding: '15px 40px',
            fontSize: '18px',
            background: '#667eea',
            color: 'white',
            border: 'none',
            borderRadius: '10px',
            cursor: 'pointer',
            fontWeight: 'bold'
          }}
        >
          Click Me!
        </button>
        <p style={{ marginTop: '20px', color: '#999', fontSize: '14px' }}>
          The full app with all components will be available once we fix the component structure.
        </p>
      </div>
    </div>
  );
}

export default SimpleApp;
