import React, { useState } from 'react';
import CreatePollFixed from './components/CreatePollFixed';
import VotePollFixed from './components/VotePollFixed';

type ViewMode = 'create' | 'view' | 'vote';

interface Poll {
  id: number;
  question: string;
  options: string[];
}

function SimpleApp() {
  const [message, setMessage] = useState('Welcome to Private Vote!');
  const [createdPolls, setCreatedPolls] = useState<Poll[]>([]);
  const [viewMode, setViewMode] = useState<ViewMode>('create');
  const [pollIdCounter, setPollIdCounter] = useState(1);

  const handleCreatePoll = (pollData: { question: string; options: string[] }) => {
    const newPoll: Poll = {
      ...pollData,
      id: pollIdCounter
    };
    setCreatedPolls([...createdPolls, newPoll]);
    setPollIdCounter(pollIdCounter + 1);
    setMessage(`Successfully created poll: "${pollData.question}"`);
    setViewMode('view');
  };

  const handleVote = (pollId: number, optionIndex: number) => {
    const poll = createdPolls.find(p => p.id === pollId);
    if (poll) {
      setMessage(`Thank you for voting! You selected: "${poll.options[optionIndex]}"`);
    }
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    }}>
      {/* Header */}
      <div style={{ textAlign: 'center', marginBottom: '30px' }}>
        <h1 style={{ 
          fontSize: '42px', 
          color: 'white',
          marginBottom: '10px',
          textShadow: '2px 2px 4px rgba(0,0,0,0.2)'
        }}>
          🗳️ Private Vote
        </h1>
        <p style={{ color: 'white', fontSize: '18px', opacity: 0.9 }}>
          Secure voting system on Solana
        </p>
      </div>

      {/* Navigation */}
      <div style={{ 
        display: 'flex', 
        gap: '10px', 
        justifyContent: 'center',
        marginBottom: '30px',
        flexWrap: 'wrap'
      }}>
        <button
          onClick={() => {
            setViewMode('create');
            setMessage('Create a new poll');
          }}
          style={{
            padding: '12px 24px',
            background: viewMode === 'create' ? 'white' : 'rgba(255,255,255,0.2)',
            color: viewMode === 'create' ? '#667eea' : 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontWeight: '600',
            fontSize: '16px',
            transition: 'all 0.3s'
          }}
        >
          Create Poll
        </button>
        <button
          onClick={() => {
            setViewMode('vote');
            setMessage(`${createdPolls.length} poll(s) available`);
          }}
          style={{
            padding: '12px 24px',
            background: viewMode === 'vote' ? 'white' : 'rgba(255,255,255,0.2)',
            color: viewMode === 'vote' ? '#667eea' : 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontWeight: '600',
            fontSize: '16px',
            transition: 'all 0.3s'
          }}
        >
          Vote ({createdPolls.length})
        </button>
        <button
          onClick={() => {
            setViewMode('view');
            setMessage(`You have ${createdPolls.length} poll(s)`);
          }}
          style={{
            padding: '12px 24px',
            background: viewMode === 'view' && createdPolls.length > 0 ? 'white' : 'rgba(255,255,255,0.2)',
            color: viewMode === 'view' && createdPolls.length > 0 ? '#667eea' : 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontWeight: '600',
            fontSize: '16px',
            transition: 'all 0.3s'
          }}
        >
          My Polls ({createdPolls.length})
        </button>
      </div>

      {/* Success Message */}
      {message && message !== 'Welcome to Private Vote!' && (
        <div style={{
          maxWidth: '600px',
          margin: '0 auto 20px',
          padding: '15px',
          background: 'rgba(76, 175, 80, 0.9)',
          color: 'white',
          borderRadius: '8px',
          textAlign: 'center',
          fontSize: '16px'
        }}>
          ✅ {message}
        </div>
      )}

      {/* Content Area */}
      {viewMode === 'create' ? (
        <CreatePollFixed onSubmit={handleCreatePoll} />
      ) : viewMode === 'vote' ? (
        <VotePollFixed polls={createdPolls} onVote={handleVote} />
      ) : viewMode === 'view' && createdPolls.length > 0 ? (
        <div style={{
          maxWidth: '800px',
          margin: '0 auto',
          display: 'grid',
          gap: '20px'
        }}>
          {createdPolls.map((poll, index) => (
            <div key={index} style={{
              background: 'white',
              padding: '25px',
              borderRadius: '12px',
              boxShadow: '0 4px 12px rgba(0,0,0,0.15)'
            }}>
              <h3 style={{ 
                fontSize: '24px', 
                marginBottom: '15px',
                color: '#333'
              }}>
                {poll.question}
              </h3>
              <div style={{ marginTop: '15px' }}>
                <strong style={{ color: '#666' }}>Options:</strong>
                <ul style={{ marginTop: '10px', paddingLeft: '20px' }}>
                  {poll.options.map((option, optIndex) => (
                    <li key={optIndex} style={{ 
                      marginBottom: '8px',
                      color: '#555',
                      fontSize: '16px'
                    }}>
                      {option}
                    </li>
                  ))}
                </ul>
              </div>
              <div style={{
                marginTop: '15px',
                padding: '10px',
                background: '#f0f4ff',
                borderRadius: '6px',
                fontSize: '14px',
                color: '#666'
              }}>
                <strong>Status:</strong> Active • <strong>Votes:</strong> 0
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div style={{
          maxWidth: '600px',
          margin: '0 auto',
          background: 'white',
          padding: '40px',
          borderRadius: '15px',
          textAlign: 'center'
        }}>
          <p style={{ fontSize: '18px', color: '#666' }}>
            No polls created yet. Click "Create Poll" to get started!
          </p>
        </div>
      )}
    </div>
  );
}

export default SimpleApp;
